pragma solidity 0.5.11;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";

contract Marketplace is ERC721 {


    constructor() ERC721() public {
        name = "main marketplace";
    }

    string public name;
    mapping(uint => Product) public products;
    uint public productCount = 0;

    struct Product {
        uint id;
        string name;
        uint price;
        address payable owner;
        bool purchased;
    }

    event ProductCreated(
        uint id,
        string name,
        uint price,
        address payable owner,
        bool purchased
    );

    event ProductPurchased(
        uint id,
        string name,
        uint price,
        address payable owner,
        bool purchased
    );

        

        function createProduct(string memory _name, uint _price) public payable {
        // Require a valid name
        require(bytes(_name).length > 0);
        // Require a valid price
        require(_price > 0);
        // Increment product count
        productCount ++;
        // Create the product
        products[productCount] = Product(productCount, _name, _price, msg.sender, false);
        _mint(msg.sender, productCount);
        approve(address(this), productCount);
        //_setTokenURI(newItemId, tokenURI);
        // Trigger an event
        emit ProductCreated(productCount, _name, _price, msg.sender, false);
    }

    function purchaseProduct(uint _id) public payable{
        //fetch the product
        Product memory _product = products[_id];
        //fetch the owner
        address payable _seller = _product.owner;
        //make sure the product is valid id
        require(_product.id > 0 && _product.id <= productCount);
        //requre that there is enough ether in the transaction
        require(msg.value >= _product.price);
        //requre that the product has not been purchased already;
        require(!_product.purchased);
        //require that the buyer is not the seller
        require(_seller != msg.sender);
        //transfer ownership to buyer
        _product.owner = msg.sender;
        // mark as purchased
        _product.purchased = true;
        //updrate the product
        products[_id] = _product;
        //payy the seller by sending flare
        address(_seller).transfer(msg.value);
        safeTransferFrom(_seller,msg.sender, _id);

        //trigger event
        emit ProductPurchased(productCount, _product.name, _product.price, msg.sender, true);
    }
}
